site_customization.stylesheet:
#footer {
    width: 100%;
    margin-top: 50px;
    padding-top: 40px;
    box-shadow: 0 -2px 6px rgba(100,100,100,0.08);
    background: #fff;
}

#footer .container {
  position: relative;
  padding: 0 10px;
  margin: 0 auto;
  max-width: 1110px;
}

#footer .container .copyright {
    float: left;
    color: #b7b7b7;
}

#footer .container .copyright .logo-footer {
  height: 20px;
  display: inline-block;
  vertical-align: middle;
  margin-right: 10px;
}

#footer .container .footer-nav {
  float: right;
  display: inline-block;
}

#footer .container .footer-nav li {
  display: inline-block;
  padding: 0 12px;
}

#footer .container .footer-nav li a {
  color: #b7b7b7;
}

#footer .container .footer-nav li a:hover {
  color: #303d49;
  transition: 0.5s;
}

#footer .fa.fa-heart {
    font-size: 0.875em;
    color: #fa6c8d;
}

